import React, { useEffect, useState } from 'react';
import api from '../utils/Api';
import Card from './card';


export default function Main({ onEditProfile, onAddCard, onEditAvatar, onCardClick }) {

    const [userName, setUserName] = useState('');
    const [userSurname, setuserSurname] = useState('');
    const [userAvatar, setUserAvatar] = useState('');
    const [cards, setCards] = useState([]);

    useEffect(() => {
      api.getUserInfo()
        .then(({ name, about, avatar }) => {
          setUserName(name);
          setuserSurname(about);
          setUserAvatar(avatar);
        })
        .catch((error) => {
          console.log(error);
        });
      api.getInitialCards()
        .then(data => {
          setCards(data);
        })
        .catch((error) => {
          console.log(error);
        });
    }, []);

    return (
      <main className="main">
        <section className="profile">
          <div className="profile__avatar-overlay">
            <img
              src={userAvatar}
              alt="Аватар пользователя"
              className="profile__avatar"
              onClick={onEditAvatar}
            />
          </div>
          <div className="profile__info">
            <h1 className="profile__info-name" aria-label="Имя пользователя">
              {userName}
            </h1>
            <button
              className="profile__info-edit-button"
              type="button"
              aria-label="Редактировать"
              onClick={onEditProfile}
            />
            <p className="profile__info-popup-job">
              {userSurname}
            </p>
          </div>
          <button
            className="profile__add-button"
            type="button"
            aria-label="Добавить"
            onClick={onAddCard}
          />
        </section>
        <section className="elements" aria-label="Карточки мест России">
          <li className="elements__element-card">
            {cards.map((card) => (
              <Card card={card} onCardClick={onCardClick} key={card._id} />
            ))}
          </li>
        </section>
      </main>
    );
  }
